package br.gov.prodigio.controle.componente;

import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.util.Base64;

import org.zkoss.image.AImage;
import org.zkoss.zk.ui.Component;
import org.zkoss.zkplus.databind.TypeConverter;

public class ImageConverter implements TypeConverter {
	static byte[] semfoto = null;
	static {
		String imagemVazio = "";
		semfoto = Base64.getDecoder().decode(imagemVazio.getBytes(StandardCharsets.UTF_8));
	}

	@Override
	public Object coerceToUi(Object val, Component comp) {
		AImage img = null;
		try {
			img = new AImage("p.jpeg", semfoto);
			if (val != null) {
				img = new AImage("p.jpeg", (byte[]) val);
			} else {
			}
		} catch (IOException e) {
			e.printStackTrace();
		}
		return img;
	}

	@Override
	public Object coerceToBean(Object val, Component comp) {
		AImage img = (AImage) val;
		if (img != null)
			return img.getByteData();
		return null;
	}

}
